tool_input.content:
{
  "mcpServers": {
    "regen-koi": {
      "command": "node",
      "args": ["${CLAUDE_PLUGIN_ROOT}/mcps/regen-koi-mcp/dist/index.js"],
      "env": {
        "KOI_API_ENDPOINT": "https://regen.gaiaai.xyz/api/koi",
        "JENA_ENDPOINT": "https://regen.gaiaai.xyz/api/koi/fuseki/koi/sparql"
      },
      "description": "Regen KOI MCP Server - Knowledge graph queries and ecological data"
    },
    "regen-network": {
      "command": "uv",
      "args": [
        "run",
        "--directory",
        "${CLAUDE_PLUGIN_ROOT}/mcps/regen-python-mcp",
        "python",
        "main.py"
      ],
      "env": {
        "PYTHONPATH": "${CLAUDE_PLUGIN_ROOT}/mcps/regen-python-mcp/src",
        "REGEN_MCP_LOG_LEVEL": "INFO"
      },
      "description": "Regen Python MCP Server - Python-based Regen Network tools"
    },
    "regen": {
      "command": "node",
      "args": ["${CLAUDE_PLUGIN_ROOT}/mcps/mcp/server/dist/index.js"],
      "env": {
        "NODE_ENV": "production"
      },
      "description": "Regen Network MCP Server - Access to Regen Ledger blockchain data"
    },
    "registry-review": {
      "command": "uv",
      "args": [
        "run",
        "--directory",
        "${CLAUDE_PLUGIN_ROOT}/mcps/regen-registry-review-mcp",
        "python",
        "-m",
        "registry_review_mcp.server"
      ],
      "env": {
        "REGISTRY_REVIEW_LLM_EXTRACTION_ENABLED": "true"
      },
      "description": "Registry Review MCP Server - Automated document review for carbon credit projects"
    }
  }
}

